import React, { useState } from 'react';
import { useMutation } from '@apollo/client';
import { ADD_USER } from '../utils/mutations';
import Auth from '../utils/auth';

function RegisterComp() {
    const [formState, setFormState] = useState({ username: '', email: '', password: '' });
    const [addUser, { error }] = useMutation(ADD_USER);

    // update state based on form input changes
    const handleChange = (event) => {
        const { name, value } = event.target;

        setFormState({
            ...formState,
            [name]: value,
        });
    };

    // submit form
    const handleFormSubmit = async event => {
        event.preventDefault();

        // use try/catch instead of promises to handle errors
        try {
            const { data } = await addUser({
                variables: { ...formState }
            });

            Auth.login(data.addUser.token);
        } catch (e) {
            console.error(e);
        }
    };

    return (
        <div classname="reg">
            <center>
                <h1> <header>Online Journal Login</header></h1>
                <form id="login_form" onSubmit={handleFormSubmit}>
                    <center>
                        <label className="login-forms">Username</label> <br /><input className="input" type="text" name="username" id="username" onChange={handleChange} /><br />
                        <label className="login-forms">Password</label><br />  <input className="input" type="password" name="password" id="password" onChange={handleChange} /><br /><br />
                        <button className="button-html">Login</button><br /><br />
                        <a href="./forgot-password.html">Forgot Password?</a>
                    </center>
                </form>
                {error && <div>Sign up failed</div>}
            </center>
        </div>
    )
}

export default RegisterComp
